export class Icon {
    constructor() {
        this.iconName = "";
        this.colorClass = "";
        this.size = 1;
        this.flip = "none";
        this.hidden = false;
        this.srHint = "";
    }
    getClassNames() {
        return {
            icon: true,
            [this.iconName]: true,
            [this.colorClass]: true,
            [`s${this.size}`]: true
        };
    }
    render() {
        return (h("sdx-animation", { animationName: this.hidden ? "hide" : "show" },
            h("sdx-flip", { direction: this.flip },
                h("span", { class: this.getClassNames(), "aria-hidden": "true" }),
                h("span", { class: "sr-only" }, this.srHint))));
    }
    static get is() { return "sdx-icon"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "colorClass": {
            "type": String,
            "attr": "color-class"
        },
        "el": {
            "elementRef": true
        },
        "flip": {
            "type": String,
            "attr": "flip"
        },
        "hidden": {
            "type": Boolean,
            "attr": "hidden"
        },
        "iconName": {
            "type": String,
            "attr": "icon-name"
        },
        "size": {
            "type": Number,
            "attr": "size"
        },
        "srHint": {
            "type": String,
            "attr": "sr-hint"
        }
    }; }
    static get style() { return "/**style-placeholder:sdx-icon:**/"; }
}
