export class ValidationMessage {
    constructor() {
        this.valid = false;
        this.validationMessage = "";
    }
    getComponentClassNames() {
        return {
            component: true
        };
    }
    render() {
        return (h("div", { class: this.getComponentClassNames(), "aria-hidden": "true" },
            h("sdx-icon", { "icon-name": "icon-exclamation-mark" }),
            " ",
            this.validationMessage));
    }
    static get is() { return "sdx-validation-message"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "valid": {
            "type": Boolean,
            "attr": "valid"
        },
        "validationMessage": {
            "type": String,
            "attr": "validation-message"
        }
    }; }
    static get style() { return "/**style-placeholder:sdx-validation-message:**/"; }
}
