export class ButtonGroup {
    constructor() {
        this.layout = "responsive";
    }
    getComponentClassNames() {
        return {
            component: true,
            [this.layout]: true
        };
    }
    hostData() {
        return {
            class: {
                [this.layout]: true
            }
        };
    }
    render() {
        return (h("div", { class: this.getComponentClassNames() },
            h("slot", null)));
    }
    static get is() { return "sdx-button-group"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "layout": {
            "type": String,
            "attr": "layout"
        }
    }; }
    static get style() { return "/**style-placeholder:sdx-button-group:**/"; }
}
